import React, {useState, useEffect} from 'react'
import axios from 'axios';
import { Link } from 'react-router-dom';

import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';

const UserList = () => {
const [users, setUser] = useState([]);

useEffect(() => {
    getUsers();
}, []);

const getUsers = async() => {
    const response  = await axios.get('http://localhost:8000/users');
    setUser(response.data);
}

const deleteUser = async(id) => {
    try {
        await axios.delete(`http://localhost:8000/users/${id}`);
        getUsers();
    } catch (error) {
        console.log(error);
    }
}
  return (
    <div className="columns mt-5 is-centered">
        <div className="column is-half">
            <Link to={`add`} className='button is-success'>Add New</Link>
           
            <DataTable paginator rows={5} rowsPerPageOptions={[5, 10, 25, 50]} tableStyle={{ minWidth: '50rem' }}>
                <Column field="name" header="Name" style={{ width: '25%' }}>Home</Column>
                <Column field="country.name" header="Country" style={{ width: '25%' }}></Column>
                <Column field="company" header="Company" style={{ width: '25%' }}></Column>
                <Column field="representative.name" header="Representative" style={{ width: '25%' }}></Column>
            </DataTable>

            <table className='table is-striped is-fullwidth'>
                <thead>
                    <tr>
                        <th>No</th>
                        <th>Name</th>
                        <th>First Name</th>
                        <th>Gender</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user, index) => (
                <tr key={user.id}>
                    <td>{index + 1}</td>
                    <td>{user.name}</td>
                    <td>{user.first}</td>
                    <td>{user.gender}</td>
                    <td>
                        <Link to={`read/${user.id}`} className='button is-small is-info'>Read</Link>
                        <Link to={`edit/${user.id}`} className='button is-small is-warning'>Edit</Link>
                        <button onClick={() => deleteUser(user.id)} className='button is-small is-danger'>Delete</button>
                    </td>
                </tr>
                    ))}
                </tbody>
            </table>
        </div>
    </div>
  )
}

export default UserList
